import {
  HINT_NOT_HIGHLIGHTED_COLOR_RGB,
  NOT_HIGHLIGHTED_COLOR_RGB,
} from "../../app/Styling/HighlightColors";
import {
  CELL_WITH_NOTES,
  HINT_BUTTON,
  HINT_CHECK_MARK,
  HINT_RIGHT_ARROW,
  LOCAL_STORAGE_ALL_LEARNED_LESSONS,
  OPEN_DRAWER_NAVIGATION,
  PLAY_DRAWER_BUTTON,
  SUDOKU_BOARD,
} from "../global/testIds";

describe("Hidden single strategy", () => {
  // Before tests insert learned lessons in localstorage
  beforeEach(() => {
    window.localStorage.setItem(
      "learned_lessons",
      LOCAL_STORAGE_ALL_LEARNED_LESSONS
    );
    window.localStorage.setItem(
      "active_game",
      '[{"puzzle":"103000900006000001009300024000006040060007813817005002090000430000009080000020000","puzzleSolution":"123574968456982371789361524932816745564297813817435692291658437375149286648723159","moves":[{"puzzleCurrentState":"103000900006000001009300024000006040060297813817435692090000430000009080000020000","puzzleCurrentNotesState":""}],"strategies":["NAKED_SINGLE","HIDDEN_SINGLE"],"difficulty":64,"drillStrategies":["NAKED_SINGLE","POINTING_TRIPLET","HIDDEN_QUADRUPLET"],"numWrongCellsPlayed":0,"numHintsUsed":95,"currentTime":1182}]'
    );
    cy.visit("");
    cy.get(OPEN_DRAWER_NAVIGATION).click();
    cy.get(PLAY_DRAWER_BUTTON).click();
    cy.contains("Resume Puzzle").click();
  });

  it("Uses hidden single hint", () => {
    cy.get(SUDOKU_BOARD).within(() => {
      cy.get(HINT_BUTTON).click();
      cy.contains("Hidden Single");
      cy.Board_Should_Have_Color_Except_For_Groups(
        -1,
        0,
        -1,
        HINT_NOT_HIGHLIGHTED_COLOR_RGB
      );
      cy.Group_Should_Only_Have_Indexes_Selected(1, 0, [1, 2, 4, 6, 7, 8]);
      cy.Cell_Should_Have_Notes_With_Colors(3, 0, "2359", "235", "");
      cy.get(HINT_RIGHT_ARROW).click();
      cy.Board_Should_Have_Color_Except_For_Groups(
        -1,
        0,
        -1,
        HINT_NOT_HIGHLIGHTED_COLOR_RGB
      );
      cy.Group_Should_Only_Have_Indexes_Selected(1, 0, [1, 2, 4, 6, 7, 8]);
      cy.get(CELL_WITH_NOTES(3, 0, "9")).should("exist");
      cy.get(HINT_CHECK_MARK).click();
      cy.Board_Should_Have_Color_Except_For_Groups(
        -1,
        -1,
        -1,
        NOT_HIGHLIGHTED_COLOR_RGB
      );
    });
  });
});
